INSERT INTO `y_news` (`id`, `category_id`, `sub_category`, `is_publish`, `is_comment`, `is_top`, `is_home`, `is_mom`, `click_count`, `ordernum`, `update_time`, `create_time`, `hardware`, `lang`, `title`, `tag`, `image`, `author`, `source`, `summary`, `seo_title`, `seo_keywords`, `seo_description`, `content`, `my_id`, `video`) VALUES
(3,	1513,	0,	1,	0,	0,	0,	0,	1,	999,	1461772800,	1461772800,	'pc',	'zh-cn',	'2013年',	NULL,	'5721c53bc3a9e.jpg',	'',	'',	'第13届中华医学会儿科学分会内分泌遗传代谢学组学术会议将叶义言教授的“中国儿童骨龄评分法”编入论文汇编书中。',	'',	'',	'',	'',	NULL,	NULL),
(4,	1513,	0,	1,	0,	0,	0,	0,	1,	999,	1461772800,	1461772800,	'pc',	'zh-cn',	'2005年',	NULL,	'5721c598784c4.jpg',	'',	'',	'叶义言骨龄方法第二本专著“中国儿童骨龄评分法”出版，Tanner教授作序，书中还强调TW3骨龄法不适合我国儿童。',	'',	'',	'',	'',	NULL,	NULL),
(5,	1513,	0,	1,	0,	0,	0,	0,	1,	999,	1461772800,	1461772800,	'pc',	'zh-cn',	'2004年',	NULL,	'5721c5b26791f.jpg',	'',	'',	'叶义言根据Tanner教授寄来的材料，在国内介绍TW3骨龄法。TW3骨龄法在TW2骨龄法的基础上，属欧洲北美儿童专用，并不适合我国儿童。',	'',	'',	'',	'',	NULL,	NULL),
(6,	1513,	0,	1,	0,	0,	0,	0,	2,	999,	1461772800,	1461772800,	'pc',	'zh-cn',	'1998年',	NULL,	'5721c5e37feff.jpg',	'',	'',	'叶义言骨龄方法被选入卫生部“十年百项”技术向全国推广。',	'',	'',	'',	'',	NULL,	NULL),
(7,	1513,	0,	1,	0,	0,	0,	0,	1,	999,	1461772800,	1461772800,	'pc',	'zh-cn',	'1993年',	NULL,	'5721c602bfd9b.jpg',	'',	'',	'叶义言基于骨龄与儿童发育的研究，不断探索，于1993年《湖南医科大学学报》发表——正常女性青春发育的个体发育',	'',	'',	'',	'',	NULL,	NULL),
(8,	1513,	0,	1,	0,	0,	0,	0,	1,	999,	1461772800,	1461772800,	'pc',	'zh-cn',	'1992年',	NULL,	'5721c614dc6c8.jpg',	'',	'',	'叶义言骨龄方法的研究成果获卫生部科技进步奖。同时叶义言还在继续研究，于1992年《湖南医科大学学报》发表——成年身高预测方法的探讨',	'',	'',	'',	'',	NULL,	NULL),
(9,	1513,	0,	1,	0,	0,	0,	0,	1,	999,	1461772800,	1461772800,	'pc',	'zh-cn',	'1992年',	NULL,	'5721c63350b64.jpg',	'',	'',	'经过几年时间的学习和研究，叶义言于1992年在《中华放射学杂志》发表——我国儿童手腕部骨骼发育期的出现年龄的研究',	'',	'',	'',	'',	NULL,	NULL),
(10,	1513,	0,	1,	0,	0,	0,	0,	1,	999,	1461772800,	1461772800,	'pc',	'zh-cn',	'1992年',	NULL,	'5721c65931fda.jpg',	'',	'',	'叶义言师从儿童生长学开创人、TW骨龄法创始人Tanner教授，成为其唯一的中国学生。协助泰勒教授研究分析，并将相关研究文章发表于《人类生物学刊》',	'',	'',	'',	'',	NULL,	NULL),
(11,	1513,	0,	1,	0,	0,	0,	0,	1,	999,	1461772800,	1461772800,	'pc',	'zh-cn',	'1991年',	NULL,	'5721c679d9ed8.jpg',	'',	'',	'叶义言正式对外发表了自己的研究成果——儿童青少年骨龄评分法及应用。此成果受到国内外骨龄研究方面的专家、学者的高度肯定，被誉为“东方型代表”',	'',	'',	'',	'',	NULL,	NULL),
(12,	1513,	0,	1,	0,	0,	0,	0,	1,	999,	1461772800,	1461772800,	'pc',	'zh-cn',	'1991年',	NULL,	'5721c69124db2.jpg',	'',	'',	'叶义言经过全面统计，总结出中国儿童骨龄评分法及其标准，为中国儿童量身订做了一把“发育尺”， 发表于湖南医科大学学报。',	'',	'',	'',	'',	NULL,	NULL),
(13,	1513,	0,	1,	0,	0,	0,	0,	1,	999,	1461772800,	1461772800,	'pc',	'zh-cn',	'1989年',	NULL,	'5721c6a43309c.jpg',	'',	'',	'叶义言经过全面统计和研究TW骨龄法在中国儿科的临床结果，在《中华儿科杂志》上发表文章——TW2在我国儿科临床应用的研究！',	'',	'',	'',	'',	NULL,	NULL),
(14,	1513,	0,	1,	0,	0,	0,	0,	1,	999,	1461772800,	1461772800,	'pc',	'zh-cn',	'1987年',	NULL,	'5721c6ba68d0b.jpg',	'',	'',	'叶义言着手研究我国儿童手腕部骨骼发育期的出现年龄，并首次在我国著名儿科杂志——《中华儿科杂志》上发表关于儿童骨骼发育的文章！',	'',	'',	'',	'',	NULL,	NULL),
